var app=new Object;app.init=function(){this.loadRoute(),this.routeListener()},app.getTemplate=function(t,e){$.ajax({url:t}).done(function(t){$(e).html(t)})},app.routes=[],app.loadRoute=function(){if(window.location.hash)app.findRoute();else{var t=app.routes[0];app.getTemplate(t.template,".content")}},app.routeListener=function(){window.onhashchange=function(){app.findRoute()}},app.findRoute=function(){var t=window.location.hash.split("#")[1];for(x in app.routes){var e=app.routes[x];e.route===t&&app.getTemplate(e.template,".content")}};